INSERT INTO `qunarComment` VALUES (712, '4185114321', '208164374', '_*3', '青海旅游第一站，西宁出发30公里到达。一般般吧。外面看有点像小型的布达拉宫。进去看还可以。参观一下保出行平安', '2018-10-10', '塔尔寺');
