INSERT INTO `qunarComment` VALUES (748, '4185114321', '207208215', '_*5', '天空很好看。塔尔寺适合有信仰或者对藏族文化感兴趣的人参观，否则就会走马观花，不知所云。', '2018-08-14', '塔尔寺');
INSERT INTO `qunarComment` VALUES (749, '4185114321', '207221912', 'M*9', '挺好的，信佛的可以一去，毕竟是布达拉宫后数第一的佛教圣地', '2018-08-14', '塔尔寺');
INSERT INTO `qunarComment` VALUES (750, '4185114321', '208590412', '黄*蒙', '和窗口买一个价。景区没有几个人', '2018-11-19', '塔尔寺');
INSERT INTO `qunarComment` VALUES (751, '4185114321', '206177520', '_*7', '让我了解了藏传佛教的很多知识&#x20;&#x20;&#x20;&#x20;&#x20;对藏传佛教有了更多的认识', '2018-05-08', '塔尔寺');
INSERT INTO `qunarComment` VALUES (752, '4185114321', '206032640', '阿*游', '感觉一般，适合有佛教信仰的人去。', '2018-04-28', '塔尔寺');
