INSERT INTO `qunarComment` VALUES (798, '4185114321', '204724559', 'a*a', 'D9:告别黑马河的日出，惊艳青海湖的油菜花，战胜日月山的海拔，目标庄严的塔尔寺--青海最有名气和代表性的藏传佛教寺院。虽然不懂藏传佛教文化，依然可以在寺院的建筑中的感受到它所蕴含的艺术与美感。点一盏灯，祈一愿福。', '2017-08-29', '塔尔寺');
INSERT INTO `qunarComment` VALUES (799, '4185114321', '208866848', '1*9', '第一次去藏庙，和常见的佛教道教寺庙不一样有点稀奇，但是门票太贵', '2018-12-28', '塔尔寺');
