INSERT INTO `qunarComment` VALUES (2695, '4185114321', '204510430', '冰*8', '塔尔寺寺庙成群远观之非常壮观，寺庙内来朝拜的佛教徒众多，他们对佛的虔诚之心令人叹之可见信仰之力的伟大，到寺内一定要许个愿据说很灵的！游塔尔寺总的来说给人带来一种震撼的感觉！', '2017-09-23', '塔尔寺');
