INSERT INTO `qunarComment` VALUES (2712, '4185114321', '204594441', '2*7', '很壮观的藏传佛教寺院，是格鲁派创始人宗喀巴大师的诞生地。里面涵盖了学校，养老院，藏医院，就是一个小世界。酥油花、壁画和堆绣被誉为“塔尔寺艺术三绝”，尤其是酥油花非常精美。值得一看。', '2017-09-17', '塔尔寺');
