INSERT INTO `qunarComment` VALUES (2930, '4185114321', '204691491', '1*3', '塔尔寺离西宁不远，去贵德路上经过。塔尔寺挺大的，只是管理不尽人意。停车场乱，去景区的路径不很清晰。马路竟然为电瓶车开出中间专道。', '2017-09-03', '塔尔寺');
