INSERT INTO `qunarComment` VALUES (2935, '4185114321', '200616209', 'k*1', '不知道为什么现在不能单独买门票，基本上都是一日游的票。塔尔寺非常值得一去，了解一下藏传佛教的文化。酥油花非常漂亮，富有极高的价值。', '2017-05-04', '塔尔寺');
