INSERT INTO `qunarComment` VALUES (17315, '3525754902', '204605408', '_*0', '青海藏文化馆位于湟中县城区内，距塔尔寺不远，共分为1号馆和2号馆。1号馆两层，一层引导我们走过藏民族的历史进程，二层向我们展示佛与神灵，以及面具、唐卡、文字与史诗、服饰与歌舞、塔尔寺艺术三绝、藏戏等多种文化艺术形式。2号馆集中展现藏传佛教独特的宇宙观和生死观。特别值得一提的是2号馆的环幕影厅，播放的电影《觉悟之路》是对藏传佛教生死观的一个解读，更是对生命意义的哲学诠释。', '2016-05-24', '青海藏文化馆');
