INSERT INTO `qunarComment` VALUES (18625, '3134116195', '205040663', '1*9', '位于青海省西宁市城东区的东关清真大寺，是中国西北地区最大最重要的伊斯兰教寺庙之一。每天来这里做礼拜的穆斯林数以万计，场面十分壮观震撼。', '2017-06-03', '东关清真大寺');
