INSERT INTO `qunarComment` VALUES (18719, '3134116195', '204163432', 'M*9', '西北四大清真寺，讲解员讲的很好，伊斯兰文化代治者理念，两世吉庆理念，人类一体理念，清真理念，慈孝理念，慈善理念。值得大家学习。', '2016-09-05', '东关清真大寺');
INSERT INTO `qunarComment` VALUES (18720, '3134116195', '204178894', '3*4', '景点值得一去，参观清真文化⋯地方不算很大', '2016-09-01', '东关清真大寺');
