INSERT INTO `qunarComment` VALUES (18722, '3134116195', '204393462', '_*4', '有幸参观了斋月节，感受伊斯兰教里穆斯林虔诚信仰，感受不一样的民族风情，可以在其中寻找自己的信仰，不一样的旅程，不一样的收获。', '2016-07-10', '东关清真大寺');
INSERT INTO `qunarComment` VALUES (18723, '3134116195', '204027681', '1*8', '这个景点在西宁，免费参观！景色一般，但后面的回民街可以体验回民生活！教堂可以祈祷，但要下午6点前！里面有回民学校，很多回民在学习，学回民语！', '2016-10-14', '东关清真大寺');
