INSERT INTO `qunarComment` VALUES (18815, '3134116195', '204219160', '心*宁', '在西宁深刻感触穆斯林教徒对真主之虔诚的不二选择。参观之中跟一位回回做了一些对话，对心中的一些疑惑找到了答案。劝人向善，心底淳朴', '2016-08-21', '东关清真大寺');
