INSERT INTO `qunarComment` VALUES (18879, '3134116195', '204573533', 'e*1', '东关清真大寺是青海省最大的清真寺，非常独特。清真大寺在西北影响力很大，听说每天都会有很多信徒在这里礼拜，礼拜场面盛大却又十分安静，令人感受到浓厚的宗教氛围。可惜今天没有礼拜，无法感受。', '2016-06-07', '东关清真大寺');
