INSERT INTO `qunarComment` VALUES (18882, '3134116195', '204331916', '1*9', '西北四大清真寺之一，西安化觉巷最古老中式风格，西宁东关清真寺也漂亮，停车极不方便。', '2016-07-24', '东关清真大寺');
