INSERT INTO `qunarComment` VALUES (18893, '3134116195', '204766152', 'y*y', '东关清真大寺。建筑很漂亮，里面的大堂很大，这里可以容纳很多人。在大寺里还跟其他旅客一起听了一个穆斯林的滔滔见解，很是受益。', '2016-02-16', '东关清真大寺');
