INSERT INTO `qunarComment` VALUES (18896, '3134116195', '204031784', '3*7', '穆斯林做礼拜，祷告的地方，古建筑与现代建筑的完美结合，历史悠久，很好的旅游景点', '2016-10-12', '东关清真大寺');
