INSERT INTO `qunarComment` VALUES (18993, '3134116195', '204663769', 'M*9', '还是让人仔细看看看了个遍。让人感叹在西宁有几座风格各异的清真寺。让人感叹，真的很漂亮，值得一观。在小雨蒙蒙中更有风味。', '2016-04-22', '东关清真大寺');
