INSERT INTO `qunarComment` VALUES (19011, '3134116195', '204691298', '3*8', '始建于公元1380年左右的明洪武年间，在青海省内规模最大，历史悠久，与西北地区著名的西安化觉寺、兰州桥门寺、新疆喀什艾提卡尔清真寺并称为西北四大清真寺。', '2016-04-05', '东关清真大寺');
