INSERT INTO `qunarComment` VALUES (19297, '2998601491', '203832730', 'w*汪', '非常值得一去，对了解藏医的发展历史很有帮助，其实藏医就是中医的一个部分，藏医的地域特色更加鲜明，发扬光大藏医就是对中医的新的贡献。', '2016-12-25', '藏医药文化博物馆');
