INSERT INTO `qunarComment` VALUES (19548, '3268684046', '139087498', '精*侠', '傍晚游览西宁广场与公园，有美妙动听的音乐喷泉，适逢旅游文化节每晚都有精彩的歌舞表演，我兴致勃勃地坐小板凳连续欣赏了两晚。有天傍晚我走上南禅寺后山山顶观赏全市夜景，万家灯火十分迷人。下山时只能借助微弱的灯光与月光小心行走，当走到南禅寺后门前，山路就顺着寺庙高大的外墙延下去了。', '2017-01-26', '西宁人民公园');
