INSERT INTO `qunarComment` VALUES (19570, '2280711881', '205033931', '达*派', '老爷山的向阳面非常陡峭，而背阳面则多了几份野趣。很多的好，唯一美中不足的是山上没有水，如果有水就更有灵性，更是名山了。', '2017-04-15', '老爷山');
