INSERT INTO `qunarComment` VALUES (19777, '4200406290', '204276990', '乔*富', '因为家人有高反，所以没有进去，在景区门口看了看，国庆人也不多，外面看很不错，景色非常美，有机会大家可以进去看看，这是有典故的地方！', '2017-10-04', '日月山');
