INSERT INTO `qunarComment` VALUES (19797, '4200406290', '204893134', 'h*r', '历史意义重大，文成公主从这里进入西藏，是吐蕃和唐朝的分界点，同时也是游牧民族和农耕民族的分界点。风景不同内地，很美，嗅到了一丝草原气息。在这骑牦牛才10元，穿下藏族衣服也10元，当地人还会牵着牦牛走起来，让我们拍视频。', '2017-07-12', '日月山');
