INSERT INTO `qunarComment` VALUES (19801, '4200406290', '203897296', '周*国', '在青海省有一座拨地而起的山峰，名为日月山，由于海拨较高，这里开辟为国家体委的高原训练基地，许多奥运选手来此训练，为国争光。', '2017-11-23', '日月山');
