INSERT INTO `qunarComment` VALUES (19814, '4200406290', '205023007', '1*1', '唐朝文成公主穿过了日月山，由此西去便是古吐蕃王国，从此远嫁松赞干布。日月山门票45元/人，穿山而过不进入也要收费。一个寺庙，还有传说与文成公主摔镜而成的日亭和月亭，分别纪念两位历史人物。走唐蕃古道，温中国历史。细细品味，收获颇丰。', '2017-01-02', '日月山');
INSERT INTO `qunarComment` VALUES (19815, '4200406290', '206624401', '1*7', '博物馆很好看，其他景点没必要看了', '2018-06-20', '日月山');
