INSERT INTO `qunarComment` VALUES (20073, '4200406290', '120298885', 'p*1', '导游妹妹非常棒，全程热心讲解热心服务，整个行程安排得也很不错，认真负责。总之很好很满意！', '2015-10-25', '日月山');
