INSERT INTO `qunarComment` VALUES (20075, '4200406290', '120316333', 'p*7', '很不错，时间安排合理，导游服务好。省了很多事情，价格实惠。', '2015-10-25', '日月山');
INSERT INTO `qunarComment` VALUES (20076, '4200406290', '204285043', '青*队', '在这里你会发现大美青海的 风光到底有多美', '2015-10-25', '日月山');
INSERT INTO `qunarComment` VALUES (20077, '4200406290', '204675855', 'z*e', '我们远观而已，就是可爱的藏族同胞骑着摩托要卖我们纪念品，最后30块买了一件手串。开心之旅啊', '2015-09-06', '日月山');
INSERT INTO `qunarComment` VALUES (20078, '4200406290', '204294428', 'm*t', '路上的一出风景，一个日山一个月山，可以爬上去眺望远处的风景', '2015-10-23', '日月山');
INSERT INTO `qunarComment` VALUES (20079, '4200406290', '120300505', 'p*0', '导游小张风趣幽默，讲解到位。大美西宁，大美青海，值得一游。', '2015-10-22', '日月山');
INSERT INTO `qunarComment` VALUES (20080, '4200406290', '204260641', '2*4', '感觉不怎么样。天气凉了，匆匆拍了照片就离开了。', '2015-10-31', '日月山');
INSERT INTO `qunarComment` VALUES (20081, '4200406290', '204321252', 'l*2', '很值得去哦！风景很优美一边日山一边月山把黄土高原和青藏高原分开了', '2015-10-19', '日月山');
