INSERT INTO `qunarComment` VALUES (20425, '3461799776', '204687736', '古*帅', '依托塔尔寺建设了八瓣莲花非物质文化遗产传承体验中心。中心汇集了民间工艺传统技艺铜银器加工、木雕、泥塑、唐卡、堆绣等，以藏族民俗生活为主的参观和体验，以藏族体育游戏、舞蹈、竞技活动为主的参观和体验。整个中心分为非物质文化遗产展示区、传承人工艺品体验区、餐饮区、购物区四大功能区，非常棒。', '2016-04-07', '八瓣莲花非物质文化体验中心');
