INSERT INTO `qunarComment` VALUES (20488, '2954068856', '203823469', '_*8', '高原明珠浦宁塔位于西宁市南，由上海市政府和西宁市政府联合修建的，是来西宁必不可少的一处景点，公交车下车需要走一段时间，塔在小山丘上', '2016-12-30', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20489, '2954068856', '204793456', 'M*6', '晴天的时候来看夕阳，直接买楼上餐厅一通票就好。', '2017-08-17', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20490, '2954068856', '204865869', '_*5', '也没什么特别的，除了顶层餐厅，其他没有什么特别有趣的地方，没多余的时间不建议过来', '2017-07-26', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20491, '2954068856', '204908354', 'M*4', '美丽的地方，可以看遍城市，值得去，就是贵了点，作介绍那个女的服务不好', '2017-07-02', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20492, '2954068856', '205095400', '2*2', '值得一去，饮食一般但景点好，看着漂亮！', '2017-05-16', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20493, '2954068856', '137295301', 'r*5', '西宁制高点  可以看到西宁全景！晚上去   万家灯火 很漂亮！', '2017-01-02', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20494, '2954068856', '130758816', 'c*6', '好评！当时买了就能使用！西宁最高点，一定要晚上去！景色超美！一层是城市展览馆。一定要坐车上山！不然累死', '2016-10-09', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20495, '2954068856', '203916192', '1*4', '蒲宁之珠观光塔座落于西宁西山，里面有旅转餐厅，全方位的，观看西宁的夜景。他仿佛在见证西宁的历史变化和日新月异的发展。是非常值得去的一个地方。', '2016-11-22', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20496, '2954068856', '55939719', '龙*王', '可以登高看到西宁市的全貌，晚上的灯光很漂亮。就是真的是太高了，还好上去的时候蹭到了员工的接送车，要不爬上去会趴下的', '2015-06-24', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20497, '2954068856', '204015839', '1*0', '一早就到了浦宁之珠，但要到10点半才开门游览，于是，在电视塔附近小巷里从容过早，感受本地居民的日常。登上电视塔观光层后，西宁一览无余。', '2016-10-20', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20498, '2954068856', '204027405', 'E*4', '浦宁之珠---上海浦东新区援建西宁建造的与东方明珠相似的塔，虽然实际塔高不及东方明珠，但它的海拔绝对高于东方明珠。塔顶的圆珠 貌似也有点东方之珠的味道哦', '2016-10-14', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20499, '2954068856', '200186939', 'o*9', '很好的旅程，租的神州的车，蛮不错的！跟大家介绍个租车电话13109757006', '2017-03-05', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20500, '2954068856', '138378331', 'a*1', '也许是因为城市夜景一般 所以感觉一般 没有什么太大的亮点 也许白天比晚上更好', '2017-01-16', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20501, '2954068856', '204977570', 'M*6', '非常漂亮，特别是晚上，夜景迷人！', '2017-02-15', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20502, '2954068856', '203943910', 'M*1', '挺不错的，晚上能看到西宁市的夜景', '2016-11-06', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20503, '2954068856', '132829692', 'y*7', '取票方便，景色能看到全西宁', '2016-11-03', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20504, '2954068856', '132829647', 'y*7', '玩得很好，服务也很周到', '2016-11-03', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20505, '2954068856', '203903031', '1*7', '西宁市区的最高点。可以观光，也可以吃饭。在餐厅里可以俯瞰整个西宁市区', '2016-11-25', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20506, '2954068856', '130047924', 'a*0（该用户已注销）', '比起别的观光塔，小了。', '2016-10-04', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20507, '2954068856', '203881607', '1*7', '塔很高，爬上去要费些力气，晚上能看到城市的夜景，还不错。', '2016-11-26', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20508, '2954068856', '204296555', '1*4', '人太多，人太多，但是上去之后拍照还不错', '2016-07-31', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20509, '2954068856', '204538610', 'M*0', '塔很高，爬上去确实要使些力气，最无语的是，当把吃奶的力气用尽后，终于到了塔顶，却发现开门营业要到早上十点半，当时却只有八点半，因我们是旅游要赶火车，不能等待，只好依依不舍得离去。', '2016-06-12', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20510, '2954068856', '204778422', 'D*0', '这里基本上算是西宁一个地标性建筑了无论在哪里个位置都能看到它，夏天景色很好，蓝色白云，登上塔顶，俯瞰整个古城西宁，大美青海风光中的一颗夜明珠！', '2016-02-11', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20511, '2954068856', '115086516', 'h*9', '非常好哦，有机会还会去的。', '2016-05-03', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20512, '2954068856', '204731763', 'M*5', '可能由于是上海援建的，所以其实就是东方明珠塔的翻版，上去参观一次需要50元，包括讲解费用，但建议大家可以免费日再去，时间是每月第一个周一和最后一个周日。', '2016-03-08', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20513, '2954068856', '72501328', 'q*6', '塔不算太高兴，但可以看到西宁全景  值得推荐', '2015-08-29', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20514, '2954068856', '204806222', '1*3', '西宁市区的标志性建筑，是一个电视塔，球形的顶端很有意思，夜景灯光很显眼。', '2016-01-18', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20515, '2954068856', '204765889', 'M*3', '非常好，景色和美食兼具，值得推荐！', '2016-02-16', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20516, '2954068856', '204776460', 'M*0', '挺好，不错，有时间可以去玩玩，值得推荐', '2016-02-12', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20517, '2954068856', '204792041', '_*6', '出了火车站稍微注意下就能看到，白天没什么看，晚上亮灯看漂亮许多，虽然我觉得那亮灯的颜色其实蛮土的', '2016-02-01', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20518, '2954068856', '204161651', '3*5', '夜晚看整个城市的夜景是一个非常不错的的地方。', '2015-12-01', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20519, '2954068856', '204419349', '_*6', '环境很不错 尤其是夜景 高原明珠名不虚传哦 很美的', '2015-10-08', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20520, '2954068856', '204229712', '1*9', '还行吧，传说塔上有旋转餐厅，没上塔去，不过吃过晚饭去还挺不错的', '2015-11-11', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20521, '2954068856', '83724559', 'p*6', '塔楼球体以玻璃幕墙的装饰为主，可以很清晰的看到外面的景致，来西宁，我个人认为一定要去浦宁之珠。', '2015-08-09', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20522, '2954068856', '204119268', 'm*8', '不错的地方，从观光电梯可透过玻璃向外远眺。塔身巨大的框架就象一个个大小不同的画框，推荐一下。', '2014-11-20', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20523, '2954068856', '204236091', '1*5', '蛮明显的，晚上远远地在西宁城内就可以看到。没必要进入玩儿啦！', '2014-08-29', '西宁浦宁之珠');
INSERT INTO `qunarComment` VALUES (20524, '2954068856', '204233803', 'e*1', '浦宁之珠据说是上海援建的 虽没有东方明珠那么大的名气 但是个人觉得上面一定能看见不一样的美', '2014-09-01', '西宁浦宁之珠');
